mapper.select:
select count(user_id)
from tb_user 
where user_id = #{user_id}

mapper.update:
  	UPDATE tb_user
	SET 
	user_pw=#{user_pw}, user_phone_number=#{user_phone_number},
	user_postcode=#{user_postcode}, user_rnameaddr=#{user_rNameAddr}, user_detailaddr=#{user_detailAddr}
	WHERE user_id=#{user_id};
